mapper.update:
update t_blog
set appreciation=#{appreciation}, commentabled=#{commentabled}, content=#{content}, create_time=#{createTime}, first_picture=#{firstPicture}, flag=#{flag}, published=#{published}, recommend=#{recommend},
    share_statement=#{shareStatement}, title=#{title}, update_time=#{updateTime}, views=#{views},`description`=#{description}, type_id=#{typeId}, tag_ids=#{tagIds}
where id=#{id};

mapper.select:
/*后台获取博客*/
   select *
   from t_blog
   where id=#{id};

/*前台获取博客的详细信息*/
   select
       b.title,b.update_time,b.first_picture,b.content,b.views,b.appreciation,b.flag,
       u.username
   from t_blog b, t_user u
   where b.user_id=u.id and b.id=#{id};

/*后台获取所有博客*/
   select b.id, t.id, b.title, t.name,  b.recommend, b.published,  b.create_time, b.update_time
   from t_blog b, t_type t
   where b.type_id = t.id order by b.update_time desc;

/*前台获取所有博客*/
  select b.id, b.first_picture, b.description, b.update_time, b.title, b.views, t.name, u.username
  from t_blog b, t_user u, t_type t
  where b.user_id=u.id and b.type_id=t.id order by b.update_time desc;

/*前台获取所有推荐博客*/
   select b.title
   from t_blog b
   where b.recommend = true
   order by b.update_time desc;

select *, year(b.update_time) year
from t_blog b
where year(b.update_time)=#{year};

select *
from t_blog b, t_type t, t_user u
where b.type_id=t.id and b.user_id=u.id and b.type_id=#{typeId};

select b.id aid, t.id tid, u.id uid, t_type.id, b.title, t.name tagname, u.username, t_type.name typename, b.recommend, b.published,  b.create_time, b.update_time, b.description, b.views, b.first_picture
from t_blog b, t_tag t, t_type, t_user u, t_blog_tags tb
where b.id=tb.blog_id and t.id=tb.tag_id and b.user_id=u.id and b.type_id=t_type.id and tb.tag_id=#{tid};
